mapper.insert:
insert into order_info (order_info_id, user_id, sku_id, 
  total_num, order_address, liaison_person, 
  phone_number, total_price, pay_way, 
  pay_datetime, order_state, gmt_create
  )
values (#{orderInfoId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{skuId,jdbcType=VARCHAR}, 
  #{totalNum,jdbcType=INTEGER}, #{orderAddress,jdbcType=VARCHAR}, #{liaisonPerson,jdbcType=VARCHAR}, 
  #{phoneNumber,jdbcType=VARCHAR}, #{totalPrice,jdbcType=DECIMAL}, #{payWay,jdbcType=VARCHAR}, 
  #{payDatetime,jdbcType=TIMESTAMP}, #{orderState,jdbcType=INTEGER}, #{gmtCreate,jdbcType=TIMESTAMP}
  )

mapper.select:
select oi.*,mu.user_name,spu.goods_name,f.file_content,GROUP_CONCAT(sv.spec_value) as spec_value from order_info oi LEFT JOIN goods_sku sku on sku.sku_id = oi.sku_id LEFT JOIN goods_spu spu on sku.spu_id = spu.spu_id LEFT JOIN goods_sku_spec_value ssv on ssv.sku_id = sku.sku_id LEFT JOIN goods_spec_value sv on sv.spec_value_id = ssv.spec_value_id LEFT JOIN goods_brand b on b.brand_id = spu.brand_id LEFT JOIN goods_category c on c.category_id = spu.category_id LEFT JOIN tb_file f on f.file_id = spu.spu_icon_id LEFT JOIN member_userinfo mu on mu.user_id = oi.user_id where 1 = 1 and oi.order_state != 5
and oi.user_id = #{user_id}
and mu.user_name like concat('%',#{user_name},'%')
and oi.order_state = #{order_state}
GROUP BY oi.order_info_id order by oi.gmt_create desc limit #{pageIndex},#{pageSize}

select count(*) as total from ( select oi.order_info_id from order_info oi LEFT JOIN goods_sku sku on sku.sku_id = oi.sku_id LEFT JOIN goods_spu spu on sku.spu_id = spu.spu_id LEFT JOIN goods_sku_spec_value ssv on ssv.sku_id = sku.sku_id LEFT JOIN goods_spec_value sv on sv.spec_value_id = ssv.spec_value_id LEFT JOIN goods_brand b on b.brand_id = spu.brand_id LEFT JOIN goods_category c on c.category_id = spu.category_id LEFT JOIN tb_file f on f.file_id = spu.spu_icon_id LEFT JOIN member_userinfo mu on mu.user_id = oi.user_id where 1 = 1 and oi.order_state != 5
and oi.user_id = #{user_id}
and mu.user_name like concat('%',#{user_name},'%')
and oi.order_state = #{order_state}
GROUP BY oi.order_info_id ) ta
